from matrix import Matrix
import reed_solomon as reed
import random
from copy import deepcopy


def corrupt_matrix_symbols(matrix_values, max_symbol_errors=5):
    """
    Corrupt up to max_symbol_errors whole symbols from the matrix bit ordering.
    A symbol consists of 8 consecutive bits in the order used during encoding.
    For each selected symbol, one random bit is flipped.
    """
    mutated = deepcopy(matrix_values)
    
    # Reconstruct the ordering: iterate in the same order as Matrix.setBits.
    valid_coords = [coord for coord, val in matrix_values.items() if val is not None]
    if not valid_coords:
        return mutated
    # Deduce the radius from the keys.
    radius = max(max(abs(q), abs(r), abs(-q - r)) for q, r in valid_coords)
    ordered_coords = []
    for q in range(-radius, radius + 1):
        r1 = max(-radius, -q - radius)
        r2 = min(radius, -q + radius)
        for r in range(r1, r2 + 1):
            if (q, r) in matrix_values and matrix_values[(q, r)] is not None:
                ordered_coords.append((q, r))
                
    # Group the ordered coordinates into symbols (chunks of 8 bits)
    symbols = [ordered_coords[i:i+8] for i in range(0, len(ordered_coords), 8)]
    # Only consider full symbols
    full_symbols = [s for s in symbols if len(s) == 8]
    
    # Randomly choose up to max_symbol_errors symbols to corrupt.
    num_errors = min(max_symbol_errors, len(full_symbols))
    error_indices = random.sample(range(len(full_symbols)), num_errors)
    
    for idx in error_indices:
        symbol_coords = full_symbols[idx]
        # Flip one random bit within the symbol.
        bit_to_flip = random.choice(symbol_coords)
        mutated[bit_to_flip] = not mutated[bit_to_flip]
        
    return mutated

